import React, { useState, useEffect } from "react";
import Stack from "@mui/material/Stack";
import Chip from "@mui/material/Chip";
import IconButton from "@mui/material/IconButton";
import NavigateNextIcon from "@mui/icons-material/NavigateNext";
import NavigateBeforeIcon from "@mui/icons-material/NavigateBefore";
import Box from "@mui/material/Box";
import { Grid } from "@mui/material";
import Typography from "@mui/material/Typography";

import Toolbar from "@mui/material/Toolbar";
import { Divider } from "@mui/material";
var stepper =1;
export default function Contactinfo({setStep}) {

  var arrayselect = [];
  const [data1, setData] = useState([]);
  const header =['ช่วงเวลาที่ติดต่อ','Topicหลัก','Topicย่อย','ข้อคำตอบ']
  const [select, setSelact] = useState([]);
  const [selected, setSelacted] = useState();
  const [layer_next, setLayer_next] = useState();


  useEffect(() => {

    async function getJSON() {
      const response = await fetch("http://localhost:3333/layer1", {
        method: "GET", // or 'PUT'
      });

      const data = await response.json();
      data.forEach(() => {
        arrayselect.push(false);
      });
      setSelact(arrayselect);
      setData(data);
    }
    getJSON();
    console.log("select",select);
  }, []);
  useEffect(() => {
    console.log('selected',selected);
  }, [selected]);
  useEffect(() => {
    console.log('layer_next',layer_next);
  }, [layer_next]);
  const handleclick = (param, param1) => {
    var array = [];
    for (let i = 0; i < data1.length; i++) {
      if (i === param - 1) {
        array.push(true);
      } else {
        array.push(false);
      }
    }
    setSelact(array);
    setSelacted(param);
    setLayer_next(param1);
  };
  function gotolayer(params, params1) {
    stepper++;

    setStep(stepper);

    console.log("stepincontactinfo",stepper);
    console.log("p", params);
    console.log("p1", params1);
    const jsonData = {
      id_header: params1,
    };
    async function postJSON(jsonData) {
      const response = await fetch("http://localhost:3333/getlayer" + params, {
        method: "POST", // or 'PUT'
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(jsonData),
      });

      const data = await response.json();
      data.forEach(() => {
        arrayselect.push(false);
      });
      setSelact(arrayselect);
      console.log(data);
      setData(data);
    }

    postJSON(jsonData);
  }
  return (
    <Grid sx={{

      display: "flex",

      alignItems: "center",
    }}><Typography>{header}</Typography>
      <Box
        component="form"
        sx={{
          marginTop: 8,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      > 
        {data1.map((row,index) => (
          <Stack
            align="center"
            direction="column"
            spacing={1}
            alignItems="center"
            margin={1}
          >
            <Chip
              label={row.description}
              key={row.id}
              id={row.id}
              color={select[index] ? "primary" : "default"}
              onClick={() => handleclick(row.id, row.layer_next)}
              sx={{
                width: "600px",
                height: "auto",
                "& .MuiChip-labelMedium": {
                  display: "flex",
                  whiteSpace: "normal",
                },
              }}
            />
          </Stack>
        ))}
      </Box>
      <Divider />
      <Toolbar>
        <Box sx={{ flexGrow: 1 }}>
          <IconButton>
            <NavigateBeforeIcon />
            <Typography>ย้อนกลับ</Typography>
          </IconButton>
        </Box>
        <Box>
          <IconButton onClick={() => gotolayer(layer_next, selected)}>
            <NavigateNextIcon />
            <Typography>ถัดไป</Typography>
          </IconButton>
        </Box>
      </Toolbar>
    </Grid>
  );
}
