import * as React from "react";
import Button from '@mui/material/Button';
import { styled, createTheme, ThemeProvider } from "@mui/material/styles";
import MuiGrid from "@mui/material/Grid";
import AppBar from "@mui/material/AppBar";
import Toolbar from "@mui/material/Toolbar";
import Divider from "@mui/material/Divider";
import Typography from "@mui/material/Typography";
import IconButton from "@mui/material/IconButton";
import Box from "@mui/material/Box";
import MenuIcon from "@mui/icons-material/Menu";
import SearchIcon from "@mui/icons-material/Search";
import MoreIcon from "@mui/icons-material/MoreVert";
import LockIcon from "@mui/icons-material/Lock";
import HistoryIcon from '@mui/icons-material/History';
import sso from "../sso.png";
const Grid = styled(MuiGrid)(({ theme }) => ({
  width: "100%",
  ...theme.typography.body2,
  '& [role="separator"]': {
    margin: theme.spacing(0, 2),
  },
}));
export default function Header() {
  const [date, setDate] = React.useState(new Date());
  React.useEffect(() => {
    const timer = setInterval(() => {
      setDate(new Date());
    }, 1000);
    return () => {
      clearInterval(timer);
    };
  }, []);
  const day = date.toLocaleDateString("th-TH", {
    year: "numeric",
    month: "long",
    day: "numeric",
  });
  const time = date.toLocaleString("th-TH", {
    hour: "numeric",
    minute: "numeric",
  });
  console.log(day);

  return (

    <AppBar
      potion="static"
      sx={{ zIndex: (theme) => theme.zIndex.drawer + 1 }}
    >
      <Toolbar>
        <Typography>{day}</Typography>

        <Divider
          orientation="vertical"
          flexItem="true"
          variant="middle"
          light
          sx={{ margin: 2 }}
        />

        <Typography sx={{ flexGrow: 1 }}> {time} น.</Typography>
        <IconButton  color="inherit">
        <LockIcon/><Typography>
          เปลี่ยนรหัสผ่าน
        </Typography>
        </IconButton>
        <Divider
          orientation="vertical"
          flexItem="true"
          variant="middle"
          light
          sx={{ margin: 2 }}
        />
        <IconButton  color="inherit">
        <HistoryIcon/><Typography>
          ประวัติการใช้งานระบบ
        </Typography>
        </IconButton>

      </Toolbar>
      <Toolbar  sx={{
        backgroundColor :"#ffffff",
      }}>  <img src={sso} width="100" height="100" />
      <Box>
        <Typography color="black" variant="h6">ระบบรับเรื่องร้องเรียน</Typography>
        <Typography color='gray' variant="subtitle2">สำนักงานประกันสังคม</Typography>
      </Box>
      </Toolbar>
    </AppBar>


  );
}
