import React, { useState, useEffect } from 'react';
import './App.css';
import { Button,TableRow,TableCell,TextField } from '@mui/material';



import CssBaseline from '@mui/material/CssBaseline';

import Link from '@mui/material/Link';

import Box from '@mui/material/Box';

import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';

function Copyright(props) {
    return (
      <Typography variant="body2" color="text.secondary" align="center" {...props}>
        {'Copyright © '}
        <Link color="inherit" href="https://mui.com/">
          Your Website
        </Link>{' '}
        {new Date().getFullYear()}
        {'.'}
      </Typography>
    );
  }


  // TODO remove, this demo shouldn't need to reset the theme.

  const defaultTheme = createTheme();




var count =2;
function Table1() {

    const [data1, setData] = useState([]);
    const [data2, setData1] = useState([]);
    const [showButton, setShowButton] = useState(false);

    const toggleButton = () => {
      setShowButton(!showButton);
    };
    const [showother, setShowother] = useState(false);

    const other = () => {
      setShowother(!showother);
    };
    useEffect(() => {

        async function getJSON() {

              const response = await fetch("http://localhost:3333/layer1", {
                method: "GET", // or 'PUT'


              });



              const data = await response.json();




            setData(data);

          }


          getJSON();

        async function getJSON1() {

          const response = await fetch("http://localhost:3333/getstatus", {
            method: "GET", // or 'PUT'


          });



          const data3 = await response.json();




        setData1(data3);

      }


      getJSON1();

      }, [])
      console.log(data1);
      console.log(data2);

      function gotolayer(params,parrams1){
        count++;
        async function getJSON() {

            const response = await fetch("http://localhost:3333/layer"+params+parrams1, {
              method: "GET", // or 'PUT'


            });



            const data = await response.json();




          setData(data);
          if(params+parrams1==6){
            setShowButton(!showButton);
            setShowother(!showother);
          }

        }


        getJSON();
      };

  return (


<ThemeProvider theme={defaultTheme}>
      <Container component="main" maxWidth="xl">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
        {data1.map((row) => (
                                  <TableRow
                                    hover
                                    tabIndex={-1}
                                    key={row.id}
                                  >
                                    <TableCell align="center">

                                    </TableCell>
                                    <TableCell>
                                    <Button onClick={() =>gotolayer(count,row.id)}>{row.description}</Button>
                                    </TableCell>

                                  </TableRow>
                                ))}

          {showother && <TextField id="other" name="other" label="ระบุรายละเอียด" variant="outlined" />}
          {showButton && <Button>ปิดเคส</Button>}
        </Box>

        <Copyright sx={{ mt: 8, mb: 4 }} />
      </Container>
    </ThemeProvider>



  );
}

export default Table1;
