import React, { useState, useEffect } from "react";
import Stack from "@mui/material/Stack";
import Chip from "@mui/material/Chip";
import IconButton from "@mui/material/IconButton";
import NavigateNextIcon from "@mui/icons-material/NavigateNext";
import NavigateBeforeIcon from "@mui/icons-material/NavigateBefore";
import Box from "@mui/material/Box";
import { Grid } from "@mui/material";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import Toolbar from "@mui/material/Toolbar";
import { Divider } from "@mui/material";
export default function Contactinfo() {
  var boolean = false;
  const [data1, setData] = useState([]);

  const [select, setSelact] = React.useState([]);

  async function getJSON() {
    const response = await fetch("http://localhost:3333/layer1", {
      method: "GET", // or 'PUT'
    });

    const data = await response.json();

    setData(data);

  }

  useEffect(() => {
    getJSON();

    for(var i=0;i<data1.length;i++){
      setSelact(select=>[...select,{boolean}])

    }
  }, []);
  const updateSelect = (index)=>(e)=>{
    const newSelect = select.map((item,i)=>{
      if(index===i){
        return{...item,[e.id]:true};
      }else{
        return item;
      }
    });
  }


  return (
    <Grid>
      <Box
        component="form"
        sx={{
          marginTop: 8,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        {data1.map((row) => (
          <Stack
            align="center"
            direction="column"
            spacing={1}
            alignItems="center"
            margin={1}
          >
            <Chip
              label={row.description}
              key={row.id}
              id={row.id}
              color={select[row.id - 1] ? "default" : "primary"}
              onClick={() => handleClick(row.id)}
            />
          </Stack>
        ))}
      </Box>
      <Divider />
      <Toolbar>
        <Box sx={{ flexGrow: 1 }}>
          <IconButton>
            <NavigateBeforeIcon />
            <Typography>ย้อนกลับ</Typography>
          </IconButton>
        </Box>
        <Box>
          <IconButton>
            <NavigateNextIcon />
            <Typography>ถัดไป</Typography>
          </IconButton>
        </Box>
      </Toolbar>
    </Grid>
  );
}
